    // Get ChatRoomUsers the user joins
    const { data } = await axios.get(
      `/api/users/likes/${userInfo.id}/`,
      config
    );

    dispatch({
      type: LIKES_LIST_SUCCESS,
      payload: data,
    });
  } catch (error) {
    dispatch({
      type: LIKES_LIST_FAIL,
      payload:
        error.response && error.response.data.detail
          ? error.response.data.detail
          : error.message,
    });
  }
};
